<script>
    import PlayIcon from '@/components/PlayIcon'
    export default {
        name:'Header',
        components:{
            PlayIcon
        },
        //接收传递数据
        props: {
            //声明了接受数据的类型，未传递时候的默认值
            backgroundColor: {
                type: [String]
            },
            icon: [String],
            iconColor: {
                type: [String],
                default: '#1E1E1E'
            },
